package JavaOutput;

public class JavaOutput {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("System.out.println(argv)");
		System.out.print("System.out.print(argv)\n");
		System.out.printf("System.out.printf(\"String\",object)\n");
		System.out.println("method System.out.print() & System.out.printf() aren't skip the row");
		System.out.println("System.out.println() 能自动换行");
		System.out.println("字符串连接符\'+\'可以连接字符串,但如果是像System.out.printf()这样可以使用格式转换的方法，当要使用格式转换时无法将多个输出的片段连接");
	}

}
